import '../styles/Thumbnail.css';

function Thumbnail(props) {
    let place = props.place;
    return(
        <article className="thumbnail">
            <div className="cover" style={{ background: 'url('+place.cover+'), linear-gradient(180deg, rgba(255, 255, 255, 0) 0%, rgba(0, 0, 0, 0.5) 100%)' }} alt={"Aperçu de : " + place.title} />
        </article>
    )
}
export default Thumbnail;
